1. /**
2.  * @fileoverview Unified Copilot CLI Runner — handles all Copilot CLI interactions.
3.  * @module agent/copilotCliRunner
4.  */
5. 
6. import * as fs from 'fs';
7. import * as path from 'path';
8. import type { ChildProcess } from 'child_process';
9. import { isCopilotCliAvailable } from './cliCheckCore';
10. import { CopilotStatsParser } from './copilotStatsParser';
11. import type { CopilotUsageMetrics } from '../plan/types';
12. import type { IProcessSpawner, ChildProcessLike } from '../interfaces/IProcessSpawner';
13. import { DefaultProcessSpawner } from '../interfaces/IProcessSpawner';
14. import type { IEnvironment } from '../interfaces/IEnvironment';
15. import { DefaultEnvironment } from '../interfaces/IEnvironment';
16. 
17. // ── Types ──────────────────────────────────────────────────────────────
18. 
19. /**
20.  * Options for running Copilot CLI.
21.  */
22. export interface CopilotRunOptions {
23.   /** The working directory (typically a worktree path) */
24.   cwd: string;
25.   /** The task/prompt to execute */
26.   task: string;
27.   /** Additional instructions or context */
28.   instructions?: string;
29.   /** Label for logging purposes */
30.   label?: string;
31.   /** Resume an existing session */
32.   sessionId?: string;
33.   /** LLM model to use (e.g., 'claude-sonnet-4.5', 'gpt-5') */
34.   model?: string;
35.   /** Directory for Copilot logs */
36.   logDir?: string;
37.   /** Path to write session share file */
38.   sharePath?: string;
39.   /** Callback for output lines */
40.   onOutput?: (line: string) => void;
41.   /** Callback when process is spawned */
42.   onProcess?: (proc: ChildProcess | ChildProcessLike) => void;
43.   /** Timeout in milliseconds (default: 5 minutes) */
44.   timeout?: number;
45.   /** Skip writing instructions file (for simple one-off prompts) */
46.   skipInstructionsFile?: boolean;
47.   /** Unique job/node ID to disambiguate instructions files across concurrent jobs */
48.   jobId?: string;
49.   /** Custom config directory for Copilot CLI session isolation. */
50.   configDir?: string;
51.   /** Additional folders the agent is allowed to access (beyond cwd/worktree). Must be absolute paths. */
52.   allowedFolders?: string[];
53.   /** URLs or URL patterns the agent is allowed to access. Secure by default (none allowed). */
54.   allowedUrls?: string[];
55. }
56. 
57. /**
58.  * Result from running Copilot CLI.
59.  */
60. export interface CopilotRunResult {
61.   success: boolean;
62.   sessionId?: string;
63.   error?: string;
64.   exitCode?: number;
65.   /** Usage metrics parsed from Copilot CLI stdout (premium requests, tokens, model breakdown, etc.) */
66.   metrics?: CopilotUsageMetrics;
67. }
68. 
69. /** Logger interface for dependency injection. */
70. export interface CopilotCliLogger {
71.   info(message: string): void;
72.   warn(message: string): void;
73.   error(message: string): void;
74.   debug(message: string): void;
75. }
76. 
77. // ── Default Logger ─────────────────────────────────────────────────────
78. 
79. const noopLogger: CopilotCliLogger = {
80.   info: () => {},
81.   warn: () => {},
82.   error: () => {},
83.   debug: () => {},
84. };
85. 
86. // ── CopilotCliRunner ───────────────────────────────────────────────────
87. 
88. /** Unified runner for Copilot CLI operations. */
89. export class CopilotCliRunner {
90.   private logger: CopilotCliLogger;
91.   private spawner: IProcessSpawner;
92.   private environment: IEnvironment;
93.   
94.   constructor(logger?: CopilotCliLogger, spawner?: IProcessSpawner, environment?: IEnvironment) {
95.     this.logger = logger ?? noopLogger;
96.     this.spawner = spawner ?? new DefaultProcessSpawner();
97.     this.environment = environment ?? new DefaultEnvironment();
98.   }
99.   
100.   /**
101.    * Check if Copilot CLI is available.
102.    */
103.   isAvailable(): boolean {
104.     return isCopilotCliAvailable();
105.   }
106.   
107.   /**
108.    * Run Copilot CLI with the given options.
109.    */
110.   async run(options: CopilotRunOptions): Promise<CopilotRunResult> {
111.     const {
112.       cwd,
113.       task,
114.       instructions,
115.       label = 'copilot',
116.       sessionId,
117.       model,
118.       logDir,
119.       sharePath,
120.       onOutput,
121.       onProcess,
122.       timeout = 300000, // 5 minutes default
123.       skipInstructionsFile = false,
124.     } = options;
125.     
126.     // Check if Copilot CLI is available
127.     if (!this.isAvailable()) {
128.       this.logger.warn(`[${label}] Copilot CLI not available`);
129.       return { success: true }; // Silent success - work can be done manually
130.     }
131.     
132.     // Setup instructions file if not skipped
133.     let instructionsFile: string | undefined;
134.     let instructionsDir: string | undefined;
135.     
136.     if (!skipInstructionsFile) {
137.       const instructionsSetup = this.writeInstructionsFile(cwd, task, instructions, label, options.jobId);
138.       instructionsFile = instructionsSetup.filePath;
139.       instructionsDir = instructionsSetup.dirPath;
140.     }
141.     
142.     // Build the command
143.     const copilotCmd = this.buildCommand({
144.       task: skipInstructionsFile ? task : `Complete the task described in the instructions file at ${instructionsFile}.`,
145.       sessionId,
146.       model,
147.       logDir,
148.       sharePath,
149.       configDir: options.configDir,
150.       cwd,
151.       allowedFolders: options.allowedFolders,
152.       allowedUrls: options.allowedUrls,
153.     });
154.     
155.     this.logger.info(`[${label}] Running: ${copilotCmd.substring(0, 100)}...`);
156.     
157.     // Execute and return result
158.     try {
159.       const result = await this.execute({
160.         command: copilotCmd,
161.         cwd,
162.         label,
163.         sessionId,
164.         timeout,
165.         onOutput,
166.         onProcess,
167.       });
168.       
169.       return result;
170.     } finally {
171.       // Cleanup instructions file
172.       if (instructionsFile) {
173.         this.cleanupInstructionsFile(instructionsFile, instructionsDir, label);
174.       }
175.     }
176.   }
177.   
178.   /**
179.    * Write instructions to .github/instructions/ in the working directory.
180.    * Public so callers with custom execution needs can still use standardized instructions.
181.    */
182.   writeInstructionsFile(
183.     cwd: string,
184.     task: string,
185.     instructions: string | undefined,
186.     label: string,
187.     jobId?: string
188.   ): { filePath: string; dirPath: string } {
189.     const instructionsDir = path.join(cwd, '.github', 'instructions');
190.     const suffix = jobId ? `-${jobId.slice(0, 8)}` : '';
191.     const instructionsFile = path.join(instructionsDir, `orchestrator-job${suffix}.instructions.md`);
192.     
193.     // Get the worktree folder name for scoping
194.     const worktreeName = path.basename(cwd);
195.     const worktreeParent = path.basename(path.dirname(cwd));
196.     const applyToScope = `${worktreeParent}/${worktreeName}/**`;
197.     
198.     // Build instructions content with frontmatter
199.     const content = `---
200. applyTo: '${applyToScope}'
201. ---
202. 
203. # Current Task
204. 
205. ${task}
206. 
207. ${instructions ? `## Additional Context\n\n${instructions}` : ''}
208. 
209. ## Guidelines
210. 
211. - Focus only on the task described above
212. - Make minimal, targeted changes
213. - Follow existing code patterns and conventions in this repository
214. - Commit your changes when complete
215. `;
216.     
217.     try {
218.       fs.mkdirSync(instructionsDir, { recursive: true });
219.       fs.writeFileSync(instructionsFile, content, 'utf8');
220.       this.logger.info(`[${label}] Wrote instructions to: ${instructionsFile}`);
221.     } catch (e) {
222.       this.logger.warn(`[${label}] Failed to write instructions file: ${e}`);
223.     }
224.     
225.     return { filePath: instructionsFile, dirPath: instructionsDir };
226.   }
227.   
228.   /** Delegates to the standalone {@link sanitizeUrl} pure function. */
229.   sanitizeUrl(raw: string): string | null {
230.     return sanitizeUrl(raw, this.logger);
231.   }
232. 
233.   /** Delegates to the standalone {@link buildCommand} pure function. */
234.   buildCommand(options: BuildCommandOptions): string {
235.     return buildCommand(options, {
236.       logger: this.logger,
237.       fallbackCwd: this.environment.cwd(),
238.     });
239.   }
240.   
241.   /**
242.    * Execute the Copilot CLI command.
243.    */
244.   private execute(options: {
245.     command: string; cwd: string; label: string; sessionId?: string;
246.     timeout: number; onOutput?: (line: string) => void;
247.     onProcess?: (proc: ChildProcess | ChildProcessLike) => void;
248.   }): Promise<CopilotRunResult> {
249.     const { command, cwd, label, sessionId, timeout, onOutput, onProcess } = options;
250.     
251.     return new Promise((resolve) => {
252.       // Clean environment: remove NODE_OPTIONS to avoid passing VS Code flags to CLI
253.       const cleanEnv = { ...this.environment.env };
254.       delete cleanEnv.NODE_OPTIONS;
255.       
256.       // Log the full invocation for diagnostics (to both logger AND onOutput for node log visibility)
257.       this.logger.info(`[${label}] Spawning: ${command}`);
258.       this.logger.info(`[${label}] CWD: ${cwd}`);
259.       const defaultKeyPrefixes = ['PATH', 'SYSTEM', 'WIN', 'COM', 'TEMP', 'TMP', 'HOME', 'USER', 'APP', 'LOCAL', 'PROGRAM', 'OS', 'PROCESSOR', 'NUMBER_OF', 'COMPUTER', 'VSCODE', 'ELECTRON', 'CHROME', 'NO_COLOR', 'PROMPT', 'PUBLIC', 'DRIVER', 'SESSION', 'LOGON', 'FPS_', 'GIT_TR', 'WVD_', 'NUGET', 'CONDA', 'PSModule', 'WSLENV', 'WT_', 'YARN', 'npm_', 'MACE', 'UATDATA', 'APPLICATION_INS', 'POWERSHELL_D', 'OneDrive', 'ESPMRepo', 'Chocolatey', 'IsDevBox', 'CLIENTNAME', 'CommonProgram'];
260.       const envLines: string[] = [];
261.       for (const [key, value] of Object.entries(cleanEnv)) {
262.         if (value && !defaultKeyPrefixes.some(p => key.toUpperCase().startsWith(p.toUpperCase()))) {
263.           const redacted = /token|key|secret|password|auth/i.test(key) ? '***' : value;
264.           envLines.push(`  ${key}=${redacted}`);
265.         }
266.       }
267.       
268.       // Emit to onOutput so it appears in the node execution log
269.       if (onOutput) {
270.         onOutput(`Spawning: ${command}`);
271.         onOutput(`CWD: ${cwd}`);
272.         if (envLines.length > 0) {
273.           onOutput(`Environment (non-default):`);
274.           for (const line of envLines) { onOutput(line); }
275.         }
276.       }
277.       // Also emit to component logger for extension output channel
278.       if (envLines.length > 0) {
279.         this.logger.debug(`[${label}] Environment (non-default keys):`);
280.         for (const line of envLines) { this.logger.debug(`[${label}] ${line}`); }
281.       }
282.       
283.       const proc = this.spawner.spawn(command, [], { cwd, shell: true, env: cleanEnv });
284.       let capturedSessionId: string | undefined = sessionId;
285.       const statsParser = new CopilotStatsParser();
286.       let timeoutHandle: NodeJS.Timeout | undefined;
287.       let wasKilledByTimeout = false;
288.       
289.       // timeout === 0 means no timeout (agent work can run indefinitely)
290.       const effectiveTimeout = timeout > 0 ? Math.min(timeout, 2147483647) : 0;
291.       if (effectiveTimeout > 0) {
292.         timeoutHandle = setTimeout(() => {
293.           wasKilledByTimeout = true;
294.           this.logger.error(`[${label}] Process timed out after ${effectiveTimeout}ms, killing PID ${proc.pid}`);
295.           try {
296.             if (this.environment.platform === 'win32') {
297.               this.spawner.spawn('taskkill', ['/pid', String(proc.pid), '/f', '/t'], { shell: true });
298.             } else {
299.               proc.kill('SIGTERM');
300.             }
301.           } catch (e) { /* ignore */ }
302.         }, effectiveTimeout);
303.       }
304. 
305.       // Track "Task complete" marker for Windows exit-code workaround
306.       let sawTaskComplete = false;
307.       
308.       if (proc.pid) {
309.         this.logger.info(`[${label}] Copilot PID: ${proc.pid}`);
310.         onProcess?.(proc);
311.       }
312.       
313.       const extractSession = (text: string) => {
314.         if (capturedSessionId) { return; }
315.         const match = text.match(/Session ID[:\s]+([a-f0-9-]{36})/i) ||
316.                      text.match(/session[:\s]+([a-f0-9-]{36})/i) ||
317.                      text.match(/Starting session[:\s]+([a-f0-9-]{36})/i);
318.         if (match) {
319.           capturedSessionId = match[1];
320.           this.logger.info(`[${label}] Captured session ID: ${capturedSessionId}`);
321.         }
322.       };
323.       
324.       proc.stdout?.on('data', (data: Buffer) => {
325.         const text = data.toString();
326.         text.split('\n').forEach(line => {
327.           if (line.trim()) {
328.             this.logger.debug(`[${label}] ${line.trim()}`);
329.             statsParser.feedLine(line.trim());
330.             onOutput?.(line.trim());
331.             if (!sawTaskComplete && line.includes('Task complete')) { sawTaskComplete = true; }
332.           }
333.         });
334.         extractSession(text);
335.       });
336.       
337.       proc.stderr?.on('data', (data: Buffer) => {
338.         const text = data.toString();
339.         text.split('\n').forEach(line => {
340.           if (line.trim()) {
341.             this.logger.debug(`[${label}] ${line.trim()}`);
342.             statsParser.feedLine(line.trim());
343.             onOutput?.(line.trim());
344.           }
345.         });
346.         extractSession(text);
347.       });
348.       
349.       proc.on('exit', (code, signal) => {
350.         if (timeoutHandle) { clearTimeout(timeoutHandle); }
351.         // Windows: code=null & signal=null after normal completion → treat as 0 if marker seen
352.         const effectiveCode = (code === null && signal === null && sawTaskComplete) ? 0 : code;
353.         const metrics = statsParser.getMetrics();
354.         if (metrics && !metrics.tokenUsage && metrics.modelBreakdown?.length) {
355.           const totals = metrics.modelBreakdown.reduce(
356.             (acc, m) => ({ input: acc.input + m.inputTokens, output: acc.output + m.outputTokens }),
357.             { input: 0, output: 0 }
358.           );
359.           metrics.tokenUsage = {
360.             inputTokens: totals.input, outputTokens: totals.output,
361.             totalTokens: totals.input + totals.output, model: metrics.modelBreakdown[0].model,
362.           };
363.         }
364. 
365.         if (effectiveCode !== 0) {
366.           let reason: string;
367.           if (wasKilledByTimeout) {
368.             reason = `Copilot CLI killed by signal TIMEOUT after ${effectiveTimeout}ms (PID ${proc.pid})`;
369.           } else if (signal) {
370.             reason = `Copilot CLI was killed by signal ${signal} (PID ${proc.pid})`;
371.           } else {
372.             reason = `Copilot CLI exited with code ${effectiveCode}`;
373.           }
374.           this.logger.error(`[${label}] ${reason}, code=${code}, signal=${signal}, sawTaskComplete=${sawTaskComplete}`);
375.           resolve({ success: false, sessionId: capturedSessionId, error: reason, exitCode: effectiveCode ?? undefined, metrics });
376.         } else {
377.           if (code === null) {
378.             this.logger.info(`[${label}] Copilot CLI completed (exit code null coerced to 0 — task completion marker was present)`);
379.           } else {
380.             this.logger.info(`[${label}] Copilot CLI completed successfully`);
381.           }
382.           resolve({ success: true, sessionId: capturedSessionId, exitCode: 0, metrics });
383.         }
384.       });
385.       
386.       proc.on('error', (err) => {
387.         if (timeoutHandle) { clearTimeout(timeoutHandle); }
388.         this.logger.error(`[${label}] Copilot CLI error: ${err.message}`);
389.         resolve({ success: false, error: err.message });
390.       });
391.     });
392.   }
393.   
394.   /**
395.    * Clean up the instructions file after execution.
396.    * Public so callers with custom execution can clean up properly.
397.    */
398.   cleanupInstructionsFile(
399.     filePath: string,
400.     dirPath: string | undefined,
401.     label: string
402.   ): void {
403.     try {
404.       if (fs.existsSync(filePath)) {
405.         fs.unlinkSync(filePath);
406.         this.logger.debug(`[${label}] Cleaned up instructions file`);
407.         
408.         // Try to remove directory if empty
409.         if (dirPath) {
410.           try {
411.             const files = fs.readdirSync(dirPath);
412.             if (files.length === 0) {
413.               fs.rmdirSync(dirPath);
414.             }
415.           } catch { /* ignore */ }
416.         }
417.       }
418.     } catch (e) {
419.       this.logger.warn(`[${label}] Failed to cleanup instructions file: ${e}`);
420.     }
421.   }
422. }
423. 
424. // ============================================================================
425. // PURE FUNCTIONS (exported for direct testing)
426. // ============================================================================
427. 
428. /**
429.  * Sanitize and validate a URL string from untrusted user input.
430.  * Pure function — no side effects. Returns the sanitized URL or null.
431.  *
432.  * @param raw - The raw URL string to validate
433.  * @param logger - Optional logger for security audit messages
434.  */
435. export function sanitizeUrl(raw: string, logger?: CopilotCliLogger): string | null {
436.   const log = logger ?? noopLogger;
437.   if (!raw || typeof raw !== 'string') {
438.     log.warn(`[SECURITY] Rejected URL: empty or non-string input`);
439.     return null;
440.   }
441.   const trimmed = raw.trim();
442.   if (trimmed.length === 0) {
443.     log.warn(`[SECURITY] Rejected URL: empty after trim`);
444.     return null;
445.   }
446.   if (/[\x00-\x1f\x7f]/.test(trimmed)) {
447.     log.warn(`[SECURITY] Rejected URL containing control characters`);
448.     return null;
449.   }
450.   if (/[`|;\n\r\\]/.test(trimmed) || /\$\(/.test(trimmed)) {
451.     log.warn(`[SECURITY] Rejected URL containing shell metacharacters: ${trimmed.substring(0, 50)}`);
452.     return null;
453.   }
454.   if (trimmed.includes('&&')) {
455.     log.warn(`[SECURITY] Rejected URL containing '&&' shell operator: ${trimmed.substring(0, 50)}`);
456.     return null;
457.   }
458.   if (trimmed.startsWith('-')) {
459.     log.warn(`[SECURITY] Rejected URL starting with dash (argument injection): ${trimmed.substring(0, 50)}`);
460.     return null;
461.   }
462.   const isWildcard = trimmed.startsWith('*.');
463.   const urlToTest = isWildcard ? `https://${trimmed.slice(2)}` : trimmed;
464.   let parsed: URL;
465.   try {
466.     parsed = new URL(urlToTest.includes('://') ? urlToTest : `https://${urlToTest}`);
467.   } catch {
468.     log.warn(`[SECURITY] Rejected URL: invalid URL format: ${trimmed.substring(0, 50)}`);
469.     return null;
470.   }
471.   if (parsed.protocol !== 'http:' && parsed.protocol !== 'https:') {
472.     log.warn(`[SECURITY] Rejected URL with disallowed scheme: ${parsed.protocol}`);
473.     return null;
474.   }
475.   if (parsed.username || parsed.password) {
476.     log.warn(`[SECURITY] Rejected URL containing embedded credentials`);
477.     return null;
478.   }
479.   return trimmed;
480. }
481. 
482. /** Options for {@link buildCommand}. */
483. export interface BuildCommandOptions {
484.   task: string;
485.   sessionId?: string;
486.   model?: string;
487.   logDir?: string;
488.   sharePath?: string;
489.   configDir?: string;
490.   cwd?: string;
491.   allowedFolders?: string[];
492.   allowedUrls?: string[];
493. }
494. 
495. /**
496.  * Build a Copilot CLI command string.
497.  * Pure function — all I/O (fs.existsSync, process.cwd) must be provided via callbacks.
498.  *
499.  * @param options  - Command options
500.  * @param deps     - Optional dependency overrides for testing
501.  */
502. export function buildCommand(
503.   options: BuildCommandOptions,
504.   deps?: {
505.     logger?: CopilotCliLogger;
506.     existsSync?: (p: string) => boolean;
507.     fallbackCwd?: string;
508.     urlSanitizer?: (raw: string) => string | null;
509.   },
510. ): string {
511.   const log = deps?.logger ?? noopLogger;
512.   const exists = deps?.existsSync ?? fs.existsSync;
513.   const { task, sessionId, model, logDir, sharePath, configDir, cwd, allowedFolders, allowedUrls } = options;
514. 
515.   const allowedPaths: string[] = [];
516.   if (cwd) {
517.     const normalizedCwd = path.resolve(cwd);
518.     if (exists(normalizedCwd)) {
519.       allowedPaths.push(normalizedCwd);
520.       log.debug(`[SECURITY] Added worktree to allowed paths: ${normalizedCwd}`);
521.     } else {
522.       log.error(`[SECURITY] Working directory does not exist: ${cwd} (normalized: ${normalizedCwd})`);
523.       allowedPaths.push(normalizedCwd);
524.     }
525.   }
526.   if (allowedFolders && allowedFolders.length > 0) {
527.     for (const folder of allowedFolders) {
528.       if (!path.isAbsolute(folder)) {
529.         log.warn(`[SECURITY] Skipping relative allowed folder (must be absolute): ${folder}`);
530.         continue;
531.       }
532.       const normalized = path.resolve(folder);
533.       if (exists(normalized)) {
534.         allowedPaths.push(normalized);
535.       } else {
536.         log.warn(`[SECURITY] Allowed folder does not exist: ${folder}`);
537.       }
538.     }
539.   }
540. 
541.   log.info(`[SECURITY] Copilot CLI allowed directories (${allowedPaths.length}):`);
542.   for (const p of allowedPaths) {
543.     log.info(`[SECURITY]   - ${p}`);
544.   }
545. 
546.   let pathsArg: string;
547.   if (allowedPaths.length === 0) {
548.     const fallbackPath = cwd || deps?.fallbackCwd || process.cwd();
549.     log.warn(`[SECURITY] No allowed paths specified, using explicit cwd: ${fallbackPath}`);
550.     pathsArg = `--add-dir ${JSON.stringify(fallbackPath)}`;
551.   } else {
552.     pathsArg = allowedPaths.map(p => `--add-dir ${JSON.stringify(p)}`).join(' ');
553.   }
554. 
555.   const doSanitize = deps?.urlSanitizer ?? ((raw: string) => sanitizeUrl(raw, log));
556.   let urlsArg = '';
557.   const sanitizedUrls: string[] = [];
558.   if (allowedUrls && allowedUrls.length > 0) {
559.     for (const rawUrl of allowedUrls) {
560.       const validated = doSanitize(rawUrl);
561.       if (validated) {
562.         sanitizedUrls.push(validated);
563.       }
564.     }
565. 
566.     if (sanitizedUrls.length > 0) {
567.       log.info(`[SECURITY] Copilot CLI allowed URLs (${sanitizedUrls.length} of ${allowedUrls.length} passed validation):`);
568.       for (const url of sanitizedUrls) {
569.         try {
570.           const parsed = new URL(url);
571.           log.info(`[SECURITY]   - ${parsed.origin}${parsed.pathname}`);
572.         } catch {
573.           log.info(`[SECURITY]   - ${url.split('?')[0].split('#')[0]}`);
574.         }
575.       }
576.       urlsArg = sanitizedUrls.map(u => `--allow-url ${JSON.stringify(u)}`).join(' ');
577.     } else {
578.       log.warn(`[SECURITY] All ${allowedUrls.length} provided URLs failed validation — network access disabled`);
579.     }
580.   } else {
581.     log.info(`[SECURITY] Copilot CLI allowed URLs: none (network access disabled)`);
582.   }
583. 
584.   let cmd = `copilot -p ${JSON.stringify(task)} --stream off ${pathsArg} --allow-all-tools`;
585.   if (urlsArg) { cmd += ` ${urlsArg}`; }
586.   if (configDir) { cmd += ` --config-dir ${JSON.stringify(configDir)}`; }
587.   if (model) { cmd += ` --model ${model}`; }
588.   if (logDir) { cmd += ` --log-dir ${JSON.stringify(logDir)} --log-level debug`; }
