       cwd,
       task,
       instructions,
       label = 'copilot',
       sessionId,
       model,
       logDir,
       sharePath,
       onOutput,
       onProcess,
       timeout = 300000, // 5 minutes default
       skipInstructionsFile = false,
     } = options;

