         if (dirPath) {
           try {
             const files = fs.readdirSync(dirPath);
             if (files.length === 0) {
               fs.rmdirSync(dirPath);
             }
           } catch { /* ignore */ }
         }
       }
     } catch (e) {
       this.logger.warn(`[${label}] Failed to cleanup instructions file: ${e}`);
     }
   }
